import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [seconds, setSeconds] = useState(10);
  const [randomNumb, setRandomNumb] = useState(0);

  const startTime = () => {
    setInterval(() => setSeconds((seconds) => seconds - 1), 1000);
  }

  useEffect(() => {
    startTime();
  }, []);

  useEffect(() => {
    if (seconds < 1) setSeconds(10);
  }, [seconds]);


  return (
    <div className="App">
     Timer
     <div>{ seconds }</div>
     <div>{`Your random Number: ${randomNumb}`}</div>
    </div>
  );
}

export default App;
