import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [seconds, setSeconds] = useState(10);
  const [randomNumb, setRandomNumb] = useState(0);

  const generateNumb = () => {
      setRandomNumb(Math.floor(Math.random() * 100) + 1);
      setSeconds(10);
  }

  useEffect(() => {
    const ms = 1000;
    setInterval(() => {
      seconds < 0 ? generateNumb() : setSeconds(seconds - 1);
    }, ms);
  }, [seconds]);


  return (
    <div className="App">
     Timer
     <div>{ seconds }</div>
     <div>{`Your random Number: ${randomNumb}`}</div>
    </div>
  );
}

export default App;
